// src/pages/SkillTreePage.js

import React, { useState, useEffect } from 'react';
import { useAuthState } from 'react-firebase-hooks/auth';
import { auth, db }     from '../firebase';
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
  arrayRemove
} from 'firebase/firestore';

export default function SkillTreePage() {
  // ‚îÄ‚îÄ‚îÄ 1Ô∏è‚É£ Auth & state ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const [user, loadingUser]     = useAuthState(auth);
  const [unlocked, setUnlocked] = useState([]);     // holds unlocked category IDs
  const [loading, setLoading]   = useState(true);

  // reference to Firestore document for this user
  const progRef = user ? doc(db, 'users', user.uid) : null;

  // load or initialize the unlocked list
  useEffect(() => {
    if (!user) return;
    getDoc(progRef).then(snap => {
      if (snap.exists()) {
        setUnlocked(snap.data().unlocked || []);
      } else {
        setDoc(progRef, { unlocked: [] });
      }
      setLoading(false);
    });
  }, [user]);

  // ‚îÄ‚îÄ‚îÄ 2Ô∏è‚É£ Category definitions ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const categories = [
    { id: 'surgery', label: 'Oral Surgery',         x: 100,       y: 150 },
    { id: 'ortho',   label: 'Orthodontics',         x: '50%',     y: 150 },
    { id: 'endo',    label: 'Endodontics',          x: 400,       y: 150 },
    { id: 'restor',  label: 'Restorative Dentistry',x: 100,       y: 300 },
    { id: 'implant', label: 'Implant Dentistry',    x: '50%',     y: 300 },
    { id: 'radio',   label: 'Radiology',            x: 400,       y: 300 },
    { id: 'tmd',     label: 'TMD / Orofacial Pain', x: '50%',     y: 450 },
  ];

  // toggle unlock state in both Firestore and local
  const toggleCategory = async id => {
    if (!user) return;
    if (unlocked.includes(id)) {
      await updateDoc(progRef, { unlocked: arrayRemove(id) });
      setUnlocked(u => u.filter(x => x !== id));
    } else {
      await updateDoc(progRef, { unlocked: arrayUnion(id) });
      setUnlocked(u => [...u, id]);
    }
  };

  if (loadingUser || loading) {
    return <div style={{ padding: 20 }}>Loading‚Ä¶</div>;
  }

  // ‚îÄ‚îÄ‚îÄ 3Ô∏è‚É£ Build all nodes & edges ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Root node:
  const rootNode = {
    id: 'root',
    label: 'Start Here',
    x: '50%',
    y: 0,
    width: 120,
    height: 40
  };

  // Category nodes:
  const categoryNodes = categories.map(cat => ({
    id: cat.id,
    label: cat.label,
    x: cat.x,
    y: cat.y,
    width: 150,
    height: 40,
    completed: unlocked.includes(cat.id)
  }));

  // Placeholder branches: for each unlocked category, a chain of 3
  const branchNodes = [];
  const branchEdges = [];
  categories.forEach(cat => {
    if (!unlocked.includes(cat.id)) return;
    const levels = [1, 2, 3];
    levels.forEach((lvl, i) => {
      const nodeId = `${cat.id}-lvl${lvl}`;
      // position each level 100px below the parent/prev level
      const parentY = i === 0 ? cat.y : (cat.y + 100 * i);
      const parentX = cat.x;
      const y = parentY + 100;
      branchNodes.push({
        id: nodeId,
        label: `${cat.label} ‚Äì Level ${lvl}`,
        x: parentX,
        y,
        width: 140,
        height: 36,
        completed: false
      });
      // edge from parent (category or prev level) to this node
      const source = i === 0 ? cat.id : `${cat.id}-lvl${i}`;
      branchEdges.push({ source, target: nodeId });
    });
  });

  // edges from root to categories
  const rootEdges = categories.map(cat => ({
    source: rootNode.id,
    target: cat.id
  }));

  // ‚îÄ‚îÄ‚îÄ 4Ô∏è‚É£ Render everything ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  return (
    <div style={{
      position: 'relative',
      width: '100vw',
      height: '100vh',
      background: '#f0f0f0'
    }}>
      {/* Header */}
      <h1 style={{
        position: 'absolute', top: 10, left: 10, zIndex: 10,
        background: '#fff', padding: '4px 8px', border: '1px solid #ccc'
      }}>
        üõ†Ô∏è Skill Tree Loaded
      </h1>

      {/* Root */}
      <Box node={rootNode} onClick={() => {}} />

      {/* Categories */}
      {categoryNodes.map(cat => (
        <Box
          key={cat.id}
          node={cat}
          onClick={() => toggleCategory(cat.id)}
        />
      ))}

      {/* Branch placeholders */}
      {branchNodes.map(n => (
        <Box key={n.id} node={n} onClick={() => {}} />
      ))}

      {/* Edges */}
      {[...rootEdges, ...branchEdges].map((e, i) => (
        <Edge key={i} from={e.source} to={e.target} />
      ))}
    </div>
  );
}

// ‚îÄ‚îÄ‚îÄ 5Ô∏è‚É£ Helper components ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

function Box({ node, onClick }) {
  const left = typeof node.x === 'number'
    ? node.x
    : `calc(${node.x} - ${node.width/2}px)`;
  return (
    <div
      onClick={onClick}
      style={{
        position: 'absolute',
        left,
        top: node.y,
        width: node.width,
        height: node.height,
        border: `2px solid ${node.completed ? 'green' : '#333'}`,
        background: node.completed ? '#e0ffe0' : '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: onClick ? 'pointer' : 'default',
        userSelect: 'none'
      }}
    >
      {node.label}
    </div>
  );
}

// Simple straight-line SVG edge
function Edge({ from, to }) {
  // locate the center of each box by querying the DOM
  const fromEl = document.querySelector(`[data-id="${from}"]`);
  const toEl   = document.querySelector(`[data-id="${to}"]`);
  if (!fromEl || !toEl) return null;

  const r1 = fromEl.getBoundingClientRect();
  const r2 = toEl.getBoundingClientRect();

  const x1 = r1.left + r1.width / 2;
  const y1 = r1.top  + r1.height;
  const x2 = r2.left + r2.width / 2;
  const y2 = r2.top; 

  return (
    <svg 
      style={{ position: 'absolute', width: '100%', height: '100%', pointerEvents: 'none' }}
    >
      <line
        x1={x1} y1={y1}
        x2={x2} y2={y2}
        stroke="#333" strokeWidth="2"
      />
    </svg>
  );
}
