// src/pages/SkillTreePage.js

import React, { useState, useEffect } from 'react';
import { useAuthState }              from 'react-firebase-hooks/auth';
import { auth, db }                  from '../firebase';
import {
  doc, getDoc, setDoc,
  updateDoc, arrayUnion, arrayRemove
} from 'firebase/firestore';

export default function SkillTreePage() {
  // 1️⃣ Auth & Firestore state
  const [user, loadingUser]     = useAuthState(auth);
  const [unlocked, setUnlocked] = useState([]);  // holds IDs: e.g. "surgery", "surgery-lvl1", etc.
  const [loadingData, setLoading] = useState(true);
  const progRef = user ? doc(db, 'users', user.uid) : null;

  useEffect(() => {
    if (!user) return;
    getDoc(progRef).then(snap => {
      if (snap.exists()) {
        setUnlocked(snap.data().unlocked || []);
      } else {
        setDoc(progRef, { unlocked: [] });
      }
      setLoading(false);
    });
  }, [user]);

  // 2️⃣ Toggle any node (category or level)
  const toggle = async id => {
    if (!user) return;
    if (unlocked.includes(id)) {
      await updateDoc(progRef, { unlocked: arrayRemove(id) });
      setUnlocked(u => u.filter(x => x !== id));
    } else {
      await updateDoc(progRef, { unlocked: arrayUnion(id) });
      setUnlocked(u => [...u, id]);
    }
  };

  if (loadingUser || loadingData) {
    return <div style={{ padding: 20 }}>Loading…</div>;
  }

  // 3️⃣ Your seven top‐level categories
  const categories = [
    { id: 'surgery', label: 'Oral Surgery',         x: 100,    y: 150 },
    { id: 'ortho',   label: 'Orthodontics',         x: '50%',  y: 150 },
    { id: 'endo',    label: 'Endodontics',          x: 400,    y: 150 },
    { id: 'restor',  label: 'Restorative Dentistry',x: 100,    y: 300 },
    { id: 'implant', label: 'Implant Dentistry',    x: '50%',  y: 300 },
    { id: 'radio',   label: 'Radiology',            x: 400,    y: 300 },
    { id: 'tmd',     label: 'TMD / Orofacial Pain', x: '50%',  y: 450 },
  ];

  // 4️⃣ Helper to decide if a given level should be shown
  const isUnlocked = id => unlocked.includes(id);

  // 5️⃣ Build a flat list of all boxes to render: categories + conditional levels
  const boxes = [];

  // top‐level categories
  categories.forEach(cat => {
    boxes.push({
      id: cat.id,
      label: cat.label,
      x: cat.x,
      y: cat.y,
      completed: isUnlocked(cat.id),
      onClick: () => toggle(cat.id)
    });

    // level1
    if (isUnlocked(cat.id)) {
      boxes.push({
        id: `${cat.id}-lvl1`,
        label: 'Level 1',
        x: cat.x,
        y: cat.y + 100,
        completed: isUnlocked(`${cat.id}-lvl1`),
        onClick: () => toggle(`${cat.id}-lvl1`)
      });
    }
    // level2
    if (isUnlocked(`${cat.id}-lvl1`)) {
      boxes.push({
        id: `${cat.id}-lvl2`,
        label: 'Level 2',
        x: cat.x,
        y: cat.y + 200,
        completed: isUnlocked(`${cat.id}-lvl2`),
        onClick: () => toggle(`${cat.id}-lvl2`)
      });
    }
    // level3
    if (isUnlocked(`${cat.id}-lvl2`)) {
      boxes.push({
        id: `${cat.id}-lvl3`,
        label: 'Level 3',
        x: cat.x,
        y: cat.y + 300,
        completed: isUnlocked(`${cat.id}-lvl3`),
        onClick: () => toggle(`${cat.id}-lvl3`)
      });
    }
  });

  // 6️⃣ Build edge list for any parent→child pairs that exist
  const edges = [];
  categories.forEach(cat => {
    // cat → lvl1
    if (isUnlocked(cat.id)) {
      edges.push({ from: cat.id, to: `${cat.id}-lvl1` });
    }
    // lvl1 → lvl2
    if (isUnlocked(`${cat.id}-lvl1`)) {
      edges.push({ from: `${cat.id}-lvl1`, to: `${cat.id}-lvl2` });
    }
    // lvl2 → lvl3
    if (isUnlocked(`${cat.id}-lvl2`)) {
      edges.push({ from: `${cat.id}-lvl2`, to: `${cat.id}-lvl3` });
    }
  });

  // 7️⃣ Render
  return (
    <div style={{
      position: 'relative',
      width: '100vw',
      height: '100vh',
      background: '#f0f0f0'
    }}>
      {/* Optional header */}
      <h1 style={{
        position: 'absolute', top: 10, left: 10, zIndex: 10,
        background: '#fff', padding: '4px 8px', border: '1px solid #ccc'
      }}>
        🛠️ Skill Tree Loaded
      </h1>

      {/* Boxes */}
      {boxes.map(b => (
        <Box key={b.id} node={b} />
      ))}

      {/* Edges */}
      {edges.map((e, i) => (
        <Edge key={i} from={e.from} to={e.to} />
      ))}
    </div>
  );
}

// ─── Box component ───────────────────────────────────────────────────────────────
function Box({ node }) {
  const left = typeof node.x === 'number'
    ? node.x
    : `calc(${node.x} - ${nodeWidth(node.id)}px)`;

  return (
    <div
      data-id={node.id}
      onClick={node.onClick}
      style={{
        position: 'absolute',
        left,
        top: node.y,
        width: 150,
        height: 40,
        border: `2px solid ${node.completed ? 'green' : '#333'}`,
        background: node.completed ? '#e0ffe0' : '#fff',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer',
        userSelect: 'none'
      }}
    >
      {node.label}
    </div>
  );
}

// ─── Edge component ──────────────────────────────────────────────────────────────
function Edge({ from, to }) {
  // get box DOM rects
  const a = document.querySelector(`[data-id="${from}"]`)?.getBoundingClientRect();
  const b = document.querySelector(`[data-id="${to}"]`)?.getBoundingClientRect();
  if (!a || !b) return null;

  const x1 = a.left + a.width/2;
  const y1 = a.top + a.height;
  const x2 = b.left + b.width/2;
  const y2 = b.top;

  return (
    <svg
      style={{
        position: 'absolute',
        width: '100%', height: '100%',
        pointerEvents: 'none'
      }}
    >
      <line x1={x1} y1={y1} x2={x2} y2={y2}
            stroke="#333" strokeWidth={2} />
    </svg>
  );
}

// approximate half‐width for centering calc; adjust if you change box width
function nodeWidth(id) {
  return 75;
}
