// src/pages/SkillTreePage.js
import React, { useState, useEffect } from 'react';
import ReactFlow, { MiniMap, Controls } from 'react-flow-renderer';
import { useAuthState } from 'react-firebase-hooks/auth';
import { auth, db } from '../firebase';
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
} from 'firebase/firestore';

const masterNodes = [
  { id: '1', type: 'input', data: { label: 'Start Here', credits: 0 }, position: { x: 250, y: 5 } },
  { id: '2', data: { label: 'Intro to Implants', credits: 5 }, position: { x: 100, y: 100 } },
  { id: '3', data: { label: 'Basic Ortho Mechanics', credits: 3 }, position: { x: 400, y: 100 } },
  // add more nodes here...
];
const masterEdges = [
  { id: 'e1-2', source: '1', target: '2', animated: true },
  { id: 'e1-3', source: '1', target: '3', animated: true },
  // add more edges...
];

export default function SkillTreePage() {
  const [user, loadingUser] = useAuthState(auth);
  const [unlocked, setUnlocked] = useState([]);        // array of node IDs
  const [totalCredits, setTotalCredits] = useState(0);
  const [loadingData, setLoadingData] = useState(true);
  const [popup, setPopup] = useState(null);             // { node, position } or null

  // Load or init user progress
  useEffect(() => {
    if (loadingUser) return;
    const progRef = doc(db, 'users', user.uid);
    getDoc(progRef).then(snap => {
      if (snap.exists()) {
        const data = snap.data();
        setUnlocked(data.unlocked || []);
        setTotalCredits(data.totalCredits || 0);
      } else {
        // initialize doc
        setDoc(progRef, { unlocked: [], totalCredits: 0 });
      }
      setLoadingData(false);
    });
  }, [user, loadingUser]);

  const handleNodeClick = (_event, node) => {
    setPopup({ node, position: node.position });
  };

  const markCompleted = async () => {
    const { node } = popup;
    const progRef = doc(db, 'users', user.uid);

    // update Firestore
    await updateDoc(progRef, {
      unlocked: arrayUnion(node.id),
      totalCredits: totalCredits + node.data.credits,
    });

    // update local state
    setUnlocked(prev => [...prev, node.id]);
    setTotalCredits(prev => prev + node.data.credits);
    setPopup(null);
  };

  if (loadingUser || loadingData) {
    return <div style={{ padding: 20 }}>Loading your progressâ€¦</div>;
  }

  // Build elements array with styling based on unlocked[]
  const elements = [
    ...masterNodes.map(n => ({
      ...n,
      style: unlocked.includes(n.id)
        ? { border: '2px solid green', background: '#e0ffe0' }
        : { opacity: 0.5 },
    })),
    ...masterEdges,
  ];

  return (
    <div style={{ height: '100vh' }}>
      <div style={{ padding: 10, background: '#fafafa', borderBottom: '1px solid #ddd' }}>
        <strong>Total CE Credits:</strong> {totalCredits}
      </div>

      <ReactFlow
        elements={elements}
        onNodeClick={handleNodeClick}
        fitView
      >
        <MiniMap />
        <Controls />
      </ReactFlow>

      {popup && (
        <div
          style={{
            position: 'absolute',
            top: popup.position.y + 50,
            left: popup.position.x + 50,
            padding: 10,
            background: 'white',
            border: '1px solid #ccc',
            zIndex: 10
          }}
        >
          <h4>{popup.node.data.label}</h4>
          <p>CE Credits: {popup.node.data.credits}</p>
          {!unlocked.includes(popup.node.id) && (
            <button onClick={markCompleted}>
              Mark Completed
            </button>
          )}
          <button onClick={() => setPopup(null)} style={{ marginLeft: 10 }}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}
