// src/pages/SkillTreePage.js

import React, { useState, useEffect } from 'react';
import { useAuthState } from 'react-firebase-hooks/auth';
import { auth, db }     from '../firebase';
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
  arrayRemove
} from 'firebase/firestore';

export default function SkillTreePage() {
  // ─── 1️⃣ Auth & state ──────────────────────────────────────────────────────────
  const [user, loadingUser]     = useAuthState(auth);
  const [unlocked, setUnlocked] = useState([]);
  const [loading, setLoading]   = useState(true);

  const progRef = user ? doc(db, 'users', user.uid) : null;
  useEffect(() => {
    if (!user) return;
    getDoc(progRef).then(snap => {
      if (snap.exists()) setUnlocked(snap.data().unlocked || []);
      else setDoc(progRef, { unlocked: [] });
      setLoading(false);
    });
  }, [user]);

  if (loadingUser || loading) {
    return <div style={{ padding: 20 }}>Loading…</div>;
  }

  // ─── 2️⃣ Category definitions with base positions ──────────────────────────────
  const categories = [
    { id: 'surgery', label: 'Oral Surgery',         baseY: 150, baseX: 100 },
    { id: 'ortho',   label: 'Orthodontics',         baseY: 150, baseX: null },
    { id: 'endo',    label: 'Endodontics',          baseY: 150, baseX: 400 },
    { id: 'restor',  label: 'Restorative Dentistry',baseY: 300, baseX: 100 },
    { id: 'implant', label: 'Implant Dentistry',    baseY: 300, baseX: null },
    { id: 'radio',   label: 'Radiology',            baseY: 300, baseX: 400 },
    { id: 'tmd',     label: 'TMD / Orofacial Pain', baseY: 450, baseX: null },
  ];

  // spacing constants
  const levelSpacing = 100;         // vertical spacing per level
  const branchLevels = 3;           // placeholder levels
  const branchHeight = branchLevels * levelSpacing; // total branch height

  // toggle unlock
  const toggleCategory = async id => {
    if (!user) return;
    if (unlocked.includes(id)) {
      await updateDoc(progRef, { unlocked: arrayRemove(id) });
      setUnlocked(prev => prev.filter(x => x !== id));
    } else {
      await updateDoc(progRef, { unlocked: arrayUnion(id) });
      setUnlocked(prev => [...prev, id]);
    }
  };

  // ─── 3️⃣ Compute rendered nodes & edges with dynamic Y offsets ─────────────────
  const root = { id: 'root', label: 'Start Here', x: '50%', y: 0 };

  let cumulativeOffset = 0;
  const categoryNodes = categories.map((cat, idx) => {
    const isUnlocked = unlocked.includes(cat.id);
    const y = cat.baseY + cumulativeOffset;
    const x = cat.baseX !== null ? cat.baseX : '50%';
    if (isUnlocked) cumulativeOffset += branchHeight;
    return { ...cat, x, y, completed: isUnlocked };
  });

  const branchNodes = [];
  const branchEdges = [];
  categoryNodes.forEach(cat => {
    if (!cat.completed) return;
    for (let lvl = 1; lvl <= branchLevels; lvl++) {
      const y = cat.y + lvl * levelSpacing;
      const id = `${cat.id}-lvl${lvl}`;
      branchNodes.push({ id, label: `${cat.label} L${lvl}`, x: cat.x, y, completed: false });
      branchEdges.push({ source: lvl === 1 ? cat.id : `${cat.id}-lvl${lvl - 1}`, target: id });
    }
  });

  const edgesRoot = categoryNodes.map(cat => ({ source: root.id, target: cat.id }));

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh', background: '#f0f0f0' }}>
      <h1 style={{ position: 'absolute', top: 10, left: 10, zIndex: 10, background: '#fff', padding: '4px 8px', border: '1px solid #ccc' }}>
        🛠️ Skill Tree Loaded
      </h1>

      {/* Root */}
      <Box node={{ id: root.id, label: root.label, x: root.x, y: root.y, width: 120, height: 40, completed: false }} />

      {/* Categories */}
      {categoryNodes.map(cat => (
        <Box
          key={cat.id}
          node={{ id: cat.id, label: cat.label, x: cat.x, y: cat.y, width: 150, height: 40, completed: cat.completed }}
          onClick={() => toggleCategory(cat.id)}
        />
      ))}

      {/* Branch placeholders */}
      {branchNodes.map(n => (
        <Box key={n.id} node={{ ...n, width: 140, height: 36 }} />
      ))}

      {/* Edges */}
      {[...edgesRoot, ...branchEdges].map((e, i) => (
        <Edge key={i} from={e.source} to={e.target} />
      ))}
    </div>
  );
}

function Box({ node, onClick }) {
  const left = typeof node.x === 'number' ? node.x : `calc(${node.x} - ${node.width/2}px)`;
  return (
    <div
      data-id={node.id}
      onClick={onClick}
      style={{ position: 'absolute', left, top: node.y, width: node.width, height: node.height, border: `2px solid ${node.completed ? 'green' : '#333'}`, background: node.completed ? '#e0ffe0' : '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', cursor: onClick ? 'pointer' : 'default', userSelect: 'none' }}
    >{node.label}</div>
  );
}

function Edge({ from, to }) {
  const f = document.querySelector(`[data-id="${from}"]`);
  const t = document.querySelector(`[data-id="${to}"]`);
  if (!f || !t) return null;
  const r1 = f.getBoundingClientRect();
  const r2 = t.getBoundingClientRect();
  const x1 = r1.left + r1.width/2;
  const y1 = r1.top + r1.height;
  const x2 = r2.left + r2.width/2;
  const y2 = r2.top;
  return (
    <svg style={{ position: 'absolute', width: '100%', height: '100%', pointerEvents: 'none' }}>
      <line x1={x1} y1={y1} x2={x2} y2={y2} stroke="#333" strokeWidth={2} />
    </svg>
  );
}
